INSERT INTO `history` VALUES (20,121,132,1,'안녕 돌머리','롱스톤','2021-10-05 18:02:19'),(23,0,280,1,'안녕하세요 한사람씨','한사람','2021-10-05 23:09:58'),(26,0,111,1,'안녕하세요 \n오늘 놀러왔어요\n드디어 놀 수 있어서 행복하네요\n지금은 경복궁 옆 서촌 카페?\n','히진공주?','2021-10-06 12:45:36'),(28,1938313335,111,1,'안녕하세요 \n오늘 놀러왔어요\n드디어 놀 수 있어서 행복하네요\n지금은 경복궁 옆 서촌 카페?\n','히진공주?','2021-10-06 12:48:24'),(29,1928513650,119,1,'유희진님께서 아주 긴글을 넣어달라고 하셔서 지금 넣고있습니다. 네 그렇습니다. 제가 누굴일까요?','뀨섭','2021-10-06 22:09:24'),(31,1928513650,130,1,'나는 데이터 넣는 기계 에헤라디야~ 나는 데이터를 넣는 기계~ 에헤라디야~ 아하하하핳하ㅏㅏ하하하하ㅏ하하하하하하하 나는 데이터를 넣고 있는 규썹이!','뀨섭','2021-10-19 22:17:10'),(32,1938313335,112,0,'오늘 너무너무 피곤하다. 잠이 막 쏟아진다. 숲속에 잠자는 공주마냥 잠이 쏟아진다. 나를 찾는 왕자님은 언제올까?','정윤쓰','2021-10-11 22:17:10'),(34,1928513650,125,1,'희진이는 바보가 아니다 \n내가 미안해 바보라고해서\n절대절대아니고 똑똑이야 최고야 최고!!!!\n','휘진이','2021-10-06 23:17:59'),(35,1928513650,69,1,'히지나 좋아해','희지니','2021-10-06 23:29:21'),(36,1928513650,80,1,'위에는 몬소리지','뀨섭','2021-10-06 23:29:21'),(37,1928513650,81,0,'나는 희망의 퐈괴자','뀨섭','2021-10-07 13:29:21'),(39,1928513650,109,1,'데이터가유지되는지 확인라고싶어','히지니에게','2021-10-07 00:00:45'),(41,1928513650,323,0,'아 오늘 너무너무 재밌었다! 다음에 또 놀자~~ ㅎㅎ',NULL,'2021-10-07 00:58:42'),(43,1928513650,202,0,'과제가 진짜 너무 많다 ㅜㅠ 진짜 죽을 것 같아,,, 제발 살려줘~~ 이러다간 다~~~~~ 죽어!!!',NULL,'2021-10-07 01:04:19'),(44,1928513650,72,0,'날짜가 이상해~~~ 미쳤어!',NULL,'2021-10-07 01:12:52'),(46,1928513650,283,0,'날짜가 이상해~~~ 미쳤어! 과연!!!!!',NULL,'2021-10-07 01:31:18'),(47,1928513650,7,0,'이번에는 진짜 될까???',NULL,'2021-10-07 01:33:49'),(63,1928513650,60,0,'이번에는 진짜 될까??? 제발제발제발!!!!',NULL,'2021-10-07 02:45:11'),(64,0,84,0,'하하하',NULL,'2021-10-07 02:58:10'),(66,1928513650,172,0,'이건 뭘까 진짜??? 짜증나게 하네?',NULL,'2021-10-07 03:07:59'),(67,1928513650,11,0,'아니 이게 모야!!!! 왜 안돼!!!!',NULL,'2021-10-07 03:20:50'),(69,1927960578,180,1,'아니 이게 모야!! 왜 안돼!!','이쁜이','2021-10-07 03:23:38'),(71,1928513650,306,0,'행복해 좋아해 고마워','0','2021-10-07 04:21:39'),(72,1927960578,355,1,'배포하자고제발','00','2021-10-08 15:21:39'),(73,1928513650,186,1,'희진아 안녕\n나는 희진이야\n나는 너한테 화가났어\n그런데 이걸로 풀어보려구 해\n고마워','희진이','2021-10-07 05:26:29'),(74,1928513650,247,1,'희진아 안녕\n나는 희진이야\n나는 너한테 화가났어\n그런데 이걸로 풀어보려구 해\n고마워','희진이','2021-10-07 05:26:44'),(75,1928513650,27,0,'오늘 개발을 너무 오래해서 힘들다\n와우\n근데 좀 재밌다',NULL,'2021-10-07 05:27:31'),(76,1928513650,283,0,'이게 저장이 듀ㅣ면 ㄹㅇ 된거더',NULL,'2021-10-07 05:29:27'),(85,1938313335,10,1,'안녕 졸업 너무너무 축하해! 비록 내가 졸업식에 가진 못했지만 그래두 이 꽃을 보내~ 우리 힘내자!','디지몽','2021-10-07 15:32:54'),(86,1922036420,310,1,'다들 특화프로젝트 너무너무 고생많았어~ 나중에 뒷풀이하쟈!!! ㅎㅎㅎ','담화팀','2021-10-07 16:03:57'),(87,1922036420,230,0,'오늘은 너무너무 바쁜 날이었어. 내일 프로젝트 발표라 너무 바쁘고 정신이 하나도 없었어. 그래도 프로젝트 끝이 보여서 좋다!',NULL,'2021-10-07 16:05:14');
